    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String ke = sec.getText();
        
        if(ke.equals("friquelmed")){
            dispose();
        }else{
            JOptionPane.showMessageDialog(null, "Clave invalida.");
            sec.setText("");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void secKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_secKeyPressed
        if (evt.getKeyCode() == 10) {
            String ke = sec.getText();
        
        if(ke.equals("friquelmed")){
            dispose();
        }else{
            JOptionPane.showMessageDialog(null, "Clave invalida.");
            sec.setText("");
        }
        }
    }//GEN-LAST:event_secKeyPressed

